import React, { useEffect, useState } from "react";
import {
  getExpenses,
  addExpense,
  updateExpense,
  deleteExpense,
  exportCSV,
  exportPDF,
} from "./api";
import ExpenseForm from "./components/ExpenseForm";
import ExpenseList from "./components/ExpenseList";
import ExpenseChart from "./components/ExpenseChart";

function App() {
  const [expenses, setExpenses] = useState([]);
  const [editingExpense, setEditingExpense] = useState(null);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    const res = await getExpenses();
    setExpenses(res.data);
  };

  const handleAddOrUpdate = async (expense) => {
    if (editingExpense) {
      await updateExpense(editingExpense.id, expense);
    } else {
      await addExpense(expense);
    }
    fetchExpenses();
  };

  const handleDelete = async (id) => {
    await deleteExpense(id);
    fetchExpenses();
  };

  const handleExportCSV = async () => {
    const res = await exportCSV();
    const url = window.URL.createObjectURL(new Blob([res.data]));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", "expenses.csv");
    document.body.appendChild(link);
    link.click();
  };

  const handleExportPDF = async () => {
    const res = await exportPDF();
    const url = window.URL.createObjectURL(new Blob([res.data], { type: "application/pdf" }));
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", "expenses.pdf");
    document.body.appendChild(link);
    link.click();
  };

  return (
    <div className="App">
      <h1>Expense Manager</h1>
      <ExpenseForm onSubmit={handleAddOrUpdate} editingExpense={editingExpense} setEditingExpense={setEditingExpense} />
      <ExpenseList expenses={expenses} onDelete={handleDelete} onEdit={setEditingExpense} />
      <button onClick={handleExportCSV}>Export CSV</button>
      <button onClick={handleExportPDF}>Export PDF</button>
      <h2>Expense Chart</h2>
      <ExpenseChart expenses={expenses} />
    </div>
  );
}

export default App;
